mapper.update:
UPDATE LOCATION
    SET LOCATION_PHONE = #{locationPhone},
        EXPLANATION = #{explanation},
        RESERVATION_LINK = #{reservationLink}
    WHERE STATUS = true
        AND USER_NO = #{userNo}

UPDATE OPERATION_TIME
SET START_TIME = #{startTime},
    END_TIME = #{endTime},
    REST_STATUS = #{restStatus}
WHERE LOCATION_NO = #{locationNo} AND DAYS = #{day}
