mapper.update:
UPDATE LOCATION
SET LOCATION_PHONE = #{locationPhone},
    EXPLANATION = #{explanation},
    RESERVATION_LINK = #{reservationLink}
WHERE STATUS = true
  AND USER_NO = #{userNo}

UPDATE OPERATION_TIME
SET START_TIME = #{startTime},
    END_TIME = #{endTime},
    REST_STATUS = #{restStatus}
WHERE LOCATION_NO = #{locationNo} AND DAYS = #{day}

mapper.select:
SELECT PET_KIND_NO, PET_KIND_NAME
FROM PET_KIND
WHERE PET_KIND_NAME = #{petKindName}

SELECT PET_SIZE_NO, PET_SIZE_NAME, PET_KIND_NO
FROM PET_SIZE
WHERE PET_SIZE_NAME = #{petSizeName}

SELECT PET_KIND_NAME
FROM PET_KIND
WHERE LOCATION_NO = #{locationNo}

SELECT PET_SIZE_NAME
FROM PET_SIZE
WHERE LOCATION_NO = #{locationNo}

SELECT ENTER_NO, LOCATION_NO, PET_SIZE_NO
FROM ENTER_GRADE
WHERE LOCATION_NO = #{locationNo}
